import React, { useState } from "react";
import "./App.css";

const App = () => {
  const [theme, setTheme] = useState("cupcake");

  const toggleTheme = (newTheme) => {
    setTheme(newTheme);
  };

  const projects = [
    {
      name: "gitprofile",
      description: "Create and deploy a dynamic portfolio by just providing your GitHub username.",
      stars: 1744,
      forks: 1672,
      language: "TypeScript",
    },
    {
      name: "ezfolio",
      description: "Open Source Portfolio/Resume CMS built using Laravel, React and Ant Design.",
      stars: 265,
      forks: 80,
      language: "PHP",
    },
    {
      name: "reactive-button",
      description: "3D animated react button component with progress bar.",
      stars: 134,
      forks: 21,
      language: "JavaScript",
    },
    // Add more projects here
  ];

  return (
    <div className={`app theme-${theme}`}>
      <header className="header">
        <h1>Ariful Alam</h1>
        <p>Senior Software Engineer | Open Source Enthusiast | PHP | Node.js | React</p>
        <button className="theme-button" onClick={() => toggleTheme(theme === "cupcake" ? "dark" : "cupcake")}>Change Theme</button>
      </header>

      <section className="profile">
        <img
          src="https://via.placeholder.com/150"
          alt="Profile"
          className="profile-picture"
        />
        <p>Based in: Bangladesh</p>
        <ul className="contact-info">
          <li>GitHub: <a href="https://github.com/arifszn">arifszn</a></li>
          <li>Twitter: <a href="https://twitter.com/arif_szn">@arif_szn</a></li>
          <li>Mastodon: <a href="https://mastodon.social">arifszn@mastodon.social</a></li>
          <li>LinkedIn: <a href="https://linkedin.com/in/ariful-alam">ariful-alam</a></li>
        </ul>
        <button className="download-button">Download Resume</button>
      </section>

      <section className="projects">
        <h2>GitHub Projects</h2>
        <div className="project-list">
          {projects.map((project, index) => (
            <div key={index} className="project-card">
              <h3>{project.name}</h3>
              <p>{project.description}</p>
              <div className="project-stats">
                <span>⭐ {project.stars}</span>
                <span>🍴 {project.forks}</span>
              </div>
              <p className="project-language">Language: {project.language}</p>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default App;
